pragma solidity >=0.8.0;
contract RandomAssignment{
    mapping(address => uint) public valueOf;
    event Assign(address indexed addr, uint indexed value);
    function assignValueFor(address addr) public {
        uint val = block.timestamp%15 + 1;
        valueOf[addr] = val;
        emit Assign(addr, val);
    }
}","language":"solidity"}],["html",{"html":"<p><span style="font-weight: 400;">In this contract we can set any address to a random value from 1 to 15, we can also change the value of the assigned address. An event is emitted each time the function </span><span style="font-weight: 400; background-color: #000000; color: #ffff00;">assignValueFor</span><span style="font-weight: 400;"> is called. (Note: This is not the recommended way to generate a random value in solidity)</span></p>"}],["html",{"html":"<p><span style="font-weight: 400;">For simplicity, we deploy this contract on the Rinkeby test net. You can use MetaMask as your wallet and get some ETH on Rinkeby through </span><a href="https://www.rinkebyfaucet.com/"><span style="font-weight: 400;">Rinkeby Faucet</span></a><span style="font-weight: 400;">. You can use Remix to deploy this contract for simplicity, please switch to injected Web3 to connect with your MetaMask in the Rinkeby test net. </span></p>
<p><span style="font-weight: 400;">Here I deployed it at the address: </span><span style="font-weight: 400; background-color: #000000; color: #ffffff;">0x261eceB52b36D931f3Ec3742a6b66Deb720d8Fe4</span><span style="font-weight: 400;"> and then I assigned some random values to some addresses using this contract (You can use this address for testing).</span></p>"}],["image",{"src":"https://lh6.googleusercontent.com/NQwzdPlyPAAsX4Cu757bjTx6xsFC_DK_7DDylHi_n3oU1jF-G1NBu-biSIxz0Tk_m9qjhSCouRDL3KNG20k6e4hSaVVebqfP2lwvhHfMWcIpTlWg3w5Hzb8-EaeLgkWOVGwoHCIN","alt":"","title":""}],["html",{"html":"<p><span style="font-weight: 400;">-&gt; Open folder project: </span><span style="background-color: #000000;"><span style="color: #ff9900;"><span style="color: #ffcc99;"><span style="font-weight: 400;">npm</span> <span style="font-weight: 400;">install</span> <span style="font-weight: 400;">-g</span></span><span style="font-weight: 400; color: #ffffff;"> @</span><span style="font-weight: 400; color: #ffcc99;">graphprotocol</span><span style="font-weight: 400; color: #ffffff;">/</span><span style="font-weight: 400; color: #ffcc99;">graph</span><span style="font-weight: 400; color: #ffffff;">-</span><span style="color: #ffcc99;"><span style="font-weight: 400;">cl</span><span style="font-weight: 400;">i</span></span></span></span><span style="font-weight: 400;"> to install graph-cli</span></p>
